import express from 'express'; // Asegúrate de que esta línea esté presente
const router = express.Router(); // Declara el router aquí

import sendEmail from '../../controllers/usuarios/sendEmail.js'; // Esta línea está bien
import { almacenarDato, almacenarDatoColmena, obtenerDatosColmena, obtenerDatosEstacion } from '../../controllers/usuarios/datosController.js';

import {
    createUsuario, 
    getUsuarios,
    getUsuarioById,
    updateUsuario,
    deleteUsuario,
    apiArduino, 
    apiEstacion,
} from '../../controllers/usuarios/usuarioController.js';

router.post('/estacion', apiEstacion);
router.post('/arduino', apiArduino);
router.post('/sendEmail', sendEmail); // Esta línea está bien

router.post('/usuarios', createUsuario);
router.get('/usuarios', getUsuarios);
router.get('/usuarios/:id', getUsuarioById);
router.put('/usuarios/:id', updateUsuario);
router.delete('/usuarios/:id', deleteUsuario);

// Se utilizo enviar los datos a la base de datos
router.post('/guardar-dato', almacenarDato);
router.post('/almacenar-dato-colmena', almacenarDatoColmena);

// Se utilizo para extraer los datos de la base de datos
router.get('/datos-estacion', obtenerDatosEstacion);
router.get('/datos-colmena', obtenerDatosColmena);

export default router;
